import React from 'react';
import { View, Text, StyleSheet, ScrollView } from 'react-native';
import { RootStackScreenProps } from '../types/navigation';
import { ServiceCard } from '../components/ServiceCard';

const services = [
  { id: 1, title: 'Ride', icon: 'üöó', route: 'RideBooking' },
  { id: 2, title: 'Long Distance', icon: 'üöê', route: 'LongDistance' },
  { id: 3, title: 'Motorbike', icon: 'üõµ', route: 'RideBooking' },
  { id: 4, title: 'Truck', icon: 'üöõ', route: 'Logistics' },
  { id: 5, title: 'Courier', icon: 'üì¶', route: 'Courier' },
  { id: 6, title: 'Shopping', icon: 'üè™', route: 'Shopping' },
  { id: 7, title: 'Food', icon: 'üçî', route: 'Food' },
  { id: 8, title: 'Lodging', icon: 'üè®', route: 'Lodging' },
  { id: 9, title: 'Walking Assistant', icon: 'üö∂', route: 'WalkingAssistant' },
  { id: 10, title: 'Building Materials', icon: 'üèóÔ∏è', route: 'BuildingMaterials' },
  { id: 11, title: 'Domestic Help', icon: 'üßπ', route: 'DomesticHelp' },
  { id: 12, title: 'Secure Transport', icon: 'üßì', route: 'SecureTransport' },
];

export const HomeScreen = ({ navigation }: RootStackScreenProps<'Home'>) => {
  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.greeting}>Hello, User!</Text>
        <Text style={styles.subtitle}>What service do you need today?</Text>
      </View>
      <View style={styles.servicesGrid}>
        {services.map((service) => (
          <ServiceCard
            key={service.id}
            title={service.title}
            icon={service.icon}
            onPress={() => navigation.navigate(service.route as any)}
          />
        ))}
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  header: {
    padding: 20,
    backgroundColor: '#f8f9fa',
  },
  greeting: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#000',
  },
  subtitle: {
    fontSize: 16,
    color: '#6c757d',
    marginTop: 5,
  },
  servicesGrid: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    padding: 10,
    gap: 10,
  },
});
