import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import { Button } from '../components/ui/Button';

export const AuthScreen = () => {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Welcome to SuperApp</Text>
      <View style={styles.buttonContainer}>
        <Button 
          onPress={() => {}} 
          variant="default"
          style={styles.button}
        >
          Sign In with Phone
        </Button>
        <Button 
          onPress={() => {}} 
          variant="outline"
          style={styles.button}
        >
          Sign In with Google
        </Button>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
    backgroundColor: '#fff',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 30,
    color: '#000',
  },
  buttonContainer: {
    width: '100%',
    gap: 10,
  },
  button: {
    width: '100%',
    marginVertical: 5,
  },
});
