import { StatusBar } from 'expo-status-bar';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { AuthScreen } from './src/screens/AuthScreen';
import { HomeScreen } from './src/screens/HomeScreen';
import { RideBooking } from './src/screens/RideBooking';
import { LongDistance } from './src/screens/LongDistance';
import { Courier } from './src/screens/Courier';
import { Profile } from './src/screens/Profile';
import { Chat } from './src/screens/Chat';
import type { RootStackParamList } from './src/types/navigation';

const Stack = createNativeStackNavigator<RootStackParamList>();

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator 
        initialRouteName="Auth"
        screenOptions={{
          headerShown: false,
          animation: 'slide_from_right'
        }}
      >
        <Stack.Screen name="Auth" component={AuthScreen} />
        <Stack.Screen name="Home" component={HomeScreen} />
        <Stack.Screen name="RideBooking" component={RideBooking} />
        <Stack.Screen name="LongDistance" component={LongDistance} />
        <Stack.Screen name="Courier" component={Courier} />
        <Stack.Screen name="Profile" component={Profile} />
        <Stack.Screen 
          name="Chat" 
          component={Chat}
          options={{
            headerShown: true,
            headerTitle: 'Chat',
            animation: 'slide_from_right'
          }}
        />
      </Stack.Navigator>
      <StatusBar style="auto" />
    </NavigationContainer>
  );
}
